import React, {useState, useRef, useEffect} from 'react';
import { Form, InputGroup, Button } from 'react-bootstrap';

import {  useConversations } from '../../contexts/ConversationsProvider';

const OpenConversation = () => {

    const [text, setText] = useState();
    const { sendMessage, selectedConversation} = useConversations();

    const handleSubmit = (e) => {
        e.preventDefault();
        sendMessage(
            selectedConversation.recipients.map(r => r.id),
            text
        );
        setText('');
    }

    useEffect(() => {
        if(lasMessageRef.current){
            lasMessageRef.current.scrollIntoView({smoot: true});        
        }
    })

    return (
        <div className='d-flex flex-column flex-grow-1'>
            <div className="flex-grow-1 overflow-auto">
                <div className='d-flex flex-column justify-content-start align-items-end px-3'>
                    {selectedConversation.messages.map((message, i) => {
                        const lasMessage = selectedConversation.messages.length -1 === indexed;
                        return (
                            <div 
                                className='my-1 d-flex flex-column'
                                key={i}
                                ref={lastMessage ? lastMessageRef : null}
                            >
                                <div
                                    className={`rounded px-2 py-1 ${message.fromMe ? 'bg-primary text-white' : 'border'}`}
                                >
                                    {message.text}
                                </div>
                                <div
                                    className={`text-muted-small ${message.fromMe ? 'text-right' : ''}`}
                                >
                                    {message.fromMe ? 'You': message.senderName}
                                </div>
                            </div>
                        )
                    })}
                </div>
            </div>
            <Form onSubmit={handleSubmit}>
                <Form.Group className='m-2'>
                    <InputGroup>
                        <Form.Control
                            as='textarea'
                            required
                            value={text}
                            onChange={e => setText(e.target.value)}
                            style={{height:'75px', resize: 'none'}}
                        />
                        <InputGroup.Append>
                            <Button type='submit'>Send</Button>
                        </InputGroup.Append>
                    </InputGroup>
                </Form.Group>
            </Form>
        </div>
    )
}

export default OpenConversation;
